            // Create a custom tile source for GeoTIFF
            const tileSource = {
              width,
              height,
              tileSize: 256,
              tileOverlap: 0,
              getTileUrl(level, x, y) {
                return `${props.imageUrl}/${level}/${x}_${y}.png`;
              },
            };

            // Initialize OpenSeadragon viewer with the custom tile source
            const viewer = OpenSeadragon({
              id: "openseadragon-viewer",
              prefixUrl: "openseadragon-images/",
              tileSources: tileSource,
              animationTime: 0.5,
              blendTime: 0.1,
              constrainDuringPan: true,
              maxZoomPixelRatio: 2,
              minZoomLevel: 1,
              visibilityRatio: 1,
              zoomPerScroll: 2,
              showNavigator: true,
              crossOriginPolicy: "Anonymous",
