import React, {useState,useEffect} from "react";
import OpenSeadragon from "openseadragon";
import './OpenSeadragon.css';

function OpenSeadragonViewer(props) {

      useEffect(() => {
        let viewer = OpenSeadragon({
          id: 'openseadragon-viewer',
          prefixUrl:'openseadragon-images/' ,
          animationTime: 0.5,
          blendTime: 0.1,
          constrainDuringPan: true,
          maxZoomPixelRatio: 2,
          minZoomLevel: 1,
          visibilityRatio: 1,
          zoomPerScroll: 2,
          showNavigator:  true,
          crossOriginPolicy: "Anonymous"
        });


        setupImage(props.imageUrl,props.imageUrl);

        function setupImage(tileSourceInput){
          viewer.close();
          let tiffTileSources = OpenSeadragon.GeoTIFFTileSource.getAllTileSources(tileSourceInput, {logLatency: true});
          tiffTileSources.then(ts=>viewer.open(ts));

          // tiffTileSources.then(tileSources=>{
          //     document.getElementById('filename').textContent += ' -- '+tileSources.length+' image'+(tileSources.length!=1?'s':'')+' found'
          // }).catch(error=>{
          //     document.getElementById('filename').textContent += ': Error opening file. Is this a valid tiff? See console for details.';
          //     console.error(error);
          // });

      }

        return () => {
          viewer && viewer.destroy();
        };
      }, [props.imageUrl]);


      function takeSS(){
        var current_view = document.getElementsByTagName("canvas");
        if (current_view){
          console.log(current_view.length);
          var my_view = current_view[0];
          var img = my_view.toDataURL("image/png");
          const link = document.createElement('a')
          link.href = img
          link.download = props.imageName
          document.body.appendChild(link)
          link.click()
          document.body.removeChild(link)
        }
      }

    return ( 
      <div>
        <button onClick={takeSS} id="print-view" >Print View</button>
        <div id="openseadragon-viewer" ></div>
      </div>
    )
}

export default OpenSeadragonViewer;



