          }
        )
        .catch(error => {
            console.log(error);
        });
    }

    return (
        <div className="get-files-container">
            {backendData!== undefined ? <RenderFile submitCount={props.fileObj.count} info ={backendData} email={props.email}/> : null} 
        </div>
    )
}

export default GetFiles;
