import React,{useState,useEffect} from "react";
import RenderFile from './RenderFile';
import './GetFiles.css'

function GetFiles(props){
    const [backendData,setBackendData] = useState([]);
    const isFirstRender = React.useRef(true);
    useEffect(() => {
        if(isFirstRender.current){
            getFiles();
            isFirstRender.current = false;
            return;
        }else{
            console.log(props.fileObj.count);
            const latest_file = props.fileObj.name.name;
            setBackendData((preValue)=>
                [...preValue,latest_file]
            )
            // console.log(backendData);
        }
    }, [props.fileObj.count]);

    function getFiles() {
        fetch("/objects/"+props.email)
        .then(response => response.json())
        .then(
          data => {
            setBackendData(data.objects)
          }
        )
        .catch(error => {
            console.log(error);
        });
    }

    return (
        <div className="get-files-container">
            {/* {backendData!== undefined ? <RenderFile submitCount={props.fileObj.count} info ={backendData} email={props.email}/> : null}  */}
        </div>
    )
}

export default GetFiles;
