import React, {useState,useEffect} from "react";
import OpenSeadragon from "openseadragon";
import './OpenSeadragon.css';
import GeoTIFF,{ fromUrl, fromUrls, fromArrayBuffer, fromBlob } from "geotiff";
import { create, open } from 'tiff.js';


function OpenSeadragonViewer(props) {

      // useEffect(() => {
      //   const viewer = OpenSeadragon({
      //     id: 'openseadragon-viewer',
      //     prefixUrl:'openseadragon-images/' ,
      //     tileSources: [{
      //       type: 'image',
      //       url: props.imageUrl,
      //       buildPyramid: false
      //     }],
      //     animationTime: 0.5,
      //     blendTime: 0.1,
      //     constrainDuringPan: true,
      //     maxZoomPixelRatio: 2,
      //     minZoomLevel: 1,
      //     visibilityRatio: 1,
      //     zoomPerScroll: 2,
      //     showNavigator:  true,
      //     crossOriginPolicy: "Anonymous"
      //   });

      //   return () => {
      //     viewer && viewer.destroy();
      //   };
      // }, [props.imageUrl]);

      useEffect(() => {
        const convertToPNG = async () => {
          try {
             // Load the TIFF image
            const response = await fetch(props.imageUrl);
            const tiffData = await response.arrayBuffer();

            // Convert the TIFF image to PNG
            const tiff = await open(tiffData);
            const canvas = await create(tiff);

            const pngUrl = canvas.toDataURL('image/png');

            // Initialize OpenSeadragon viewer with the PNG image
            const viewer = OpenSeadragon({
              id: 'openseadragon-viewer',
              prefixUrl: 'openseadragon-images/',
              tileSources: [{
                type: 'image',
                url: pngUrl,
                buildPyramid: false
              }],
              animationTime: 0.5,
              blendTime: 0.1,
              constrainDuringPan: true,
              maxZoomPixelRatio: 2,
              minZoomLevel: 1,
              visibilityRatio: 1,
              zoomPerScroll: 2,
              showNavigator: true,
              crossOriginPolicy: 'Anonymous'
            });

            // Cleanup function
            return () => {
              viewer && viewer.destroy();
              URL.revokeObjectURL(pngUrl);
            };
          } catch (error) {
            console.error('Failed to convert and load image:', error);
          }
        };

        convertToPNG();
      }, [props.imageUrl]);



      function takeSS(){
        var current_view = document.getElementsByTagName("canvas");
        if (current_view){
          console.log(current_view.length);
          var my_view = current_view[0];
          var img = my_view.toDataURL("image/png");
          const link = document.createElement('a')
          link.href = img
          link.download = props.imageName
          document.body.appendChild(link)
          link.click()
          document.body.removeChild(link)
        }
      }

    return ( 
      <div>
        <button onClick={takeSS} id="print-view" >Print View</button>
        <div id="openseadragon-viewer" ></div>
      </div>
    )
}

export default OpenSeadragonViewer;



