import React,{useState,useEffect} from "react";
import RenderFile from './RenderFile';
import './GetFiles.css'

function GetFiles(props){
    const [backendData,setBackendData] = useState([{}]);
    const isFirstRender = React.useRef(true);
    useEffect(() => {
        if(isFirstRender.current){
            getFiles();
            isFirstRender.current = false;
            return;
        }else{
            const latest_file = props.fileObj.name;
            // setBackendData((preValue)=> {[
            //     ...preValue,
            //     {[preValue.length] : latest_file}

            // ]});
            setBackendData(prevValue => [
                ...prevValue,
                { [Object.keys(prevValue).length]: latest_file }
            ]);
            console.log(backendData);
        }
    }, [props.fileObj.count]);

    function getFiles() {
        fetch("/objects/"+props.email)
        .then(response => response.json())
        .then(
          data => {
            setBackendData(data)
          }
        )
        console.log(backendData);
    }

    return (
        <div className="get-files-container">
            {backendData.objects!== undefined ? <RenderFile submitCount={props.fileObj.count} info ={backendData.objects} email={props.email}/> : null} 
        </div>
    )
}

export default GetFiles;
